package lecture6;

public class typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	byte a = 14;
	System.out.println(a);
		int i = 428;
		a = (byte) i; 
		System.out.println(a);
		
		int ii = 178;
		byte b1 = 62;
		ii=b1;
		System.out.println(ii);
	}

}
